# views.py\nfrom django.shortcuts import render\nfrom django.core.paginator import Paginator\nfrom .models import YourModel\n\ndef ajax_table(request):\n    model_list = YourModel.objects.all()\n    paginator = Paginator(model_list, 10)  # Show 10 items per page\n    page_number = request.GET.get('page')\n    page_obj = paginator.get_page(page_number)\n    if request.is_ajax():\n        return render(request, 'table_partial.html', {'page_obj': page_obj})\n    return render(request, 'table.html', {'page_obj': page_obj})