# app.py\nimport cherrypy\nfrom tasks import long_running_task\n\nclass MyApp:\n    @cherrypy.expose\n    def index(self):\n        return "Welcome to the web hosting control panel!"\n\n    @cherrypy.expose\n    def start_task(self, arg1, arg2):\n        # Start a long-running task\n        result = long_running_task.delay(arg1, arg2)\n        return f"Task started with ID: {result.id}"\n\n    @cherrypy.expose\n    def task_status(self, task_id):\n        result = long_running_task.AsyncResult(task_id)\n        return f"Task status: {result.status}"\n\nif __name__ == '__main__':\n    cherrypy.quickstart(MyApp())