import fitz  # PyMuPDF\n\ndef open_pdf_at_page(file_path, page_number):\n    # Open the PDF file\n    pdf_document = fitz.open(file_path)\n    \n    # Ensure the page number is within the valid range\n    if page_number < 1 or page_number > len(pdf_document):\n        raise ValueError("Page number out of range")\n    \n    # Get the specified page\n    page = pdf_document.load_page(page_number - 1)  # Page numbers are 0-based in PyMuPDF\n    \n    # Display the page (this will depend on how you want to handle the display)\n    # For simplicity, let's just print the text of the page\n    print(page.get_text("text"))\n\n# Example usage\nopen_pdf_at_page("example.pdf", 3)