import fitz  # PyMuPDF\n\ndef crop_pdf(input_path, output_path, crop_rect):\n    # Open the PDF file\n    pdf_document = fitz.open(input_path)\n    \n    # Iterate over each page\n    for page_number in range(len(pdf_document)):\n        page = pdf_document[page_number]\n        \n        # Set the crop box to the specified rectangle\n        page.set_cropbox(crop_rect)\n    \n    # Save the modified PDF to a new file\n    pdf_document.save(output_path)\n    pdf_document.close()\n\n# Define the cropping rectangle (left, top, right, bottom)\n# Example: Cropping to a 500x500 box starting from (50, 50) to (550, 550)\ncrop_rectangle = fitz.Rect(50, 50, 550, 550)\n\n# Input and output PDF file paths\ninput_pdf_path = 'input.pdf'\noutput_pdf_path = 'cropped_output.pdf'\n\n# Perform the cropping\ncrop_pdf(input_pdf_path, output_pdf_path, crop_rectangle)