# appstate.py\n\n# Global variables\nconnected_users = []\ntotal_answers = 0\nconfig = {}\n\n# Functions to manipulate the global state\ndef get_user_list():\n    return connected_users\n\ndef add_user(user):\n    connected_users.append(user)\n\ndef remove_user(user):\n    connected_users.remove(user)\n\ndef get_total_answers():\n    return total_answers\n\ndef increment_answers():\n    global total_answers\n    total_answers += 1\n\ndef set_config(new_config):\n    global config\n    config = new_config\n\ndef get_config():\n    return config