import fitz  # PyMuPDF\n\ndef split_pdf(input_pdf_path, output_folder):\n    # Open the input PDF file\n    pdf_document = fitz.open(input_pdf_path)\n    \n    # Iterate through each page in the PDF\n    for page_num in range(pdf_document.page_count):\n        # Select the page\n        page = pdf_document.load_page(page_num)\n        \n        # Create a new PDF with the single page\n        single_page_pdf = fitz.open()\n        single_page_pdf.insert_pdf(pdf_document, from_page=page_num, to_page=page_num)\n        \n        # Save the single page PDF\n        output_pdf_path = f"{output_folder}/page_{page_num + 1}.pdf"\n        single_page_pdf.save(output_pdf_path)\n        single_page_pdf.close()\n    \n    # Close the input PDF document\n    pdf_document.close()\n\n# Example usage\ninput_pdf_path = "path/to/input.pdf"\noutput_folder = "path/to/output/folder"\nsplit_pdf(input_pdf_path, output_folder)