import fitz  # PyMuPDF\n\ndef sanitize_pdf(file_path, output_path):\n    try:\n        doc = fitz.open(file_path)\n        doc.set_metadata({})\n        for page in doc:\n            for img in page.get_images(full=True):\n                xref = img[0]\n                pix = fitz.Pixmap(doc, xref)\n                if pix.n < 5:  # this is GRAY or RGB\n                    pix.writePNG(f"temp_image.png")\n                    img = fitz.open("temp_image.png")\n                    page.insert_image(page.rect, stream=img.write())\n        doc.save(output_path, garbage=4, deflate=True)\n        print("PDF sanitized successfully.")\n    except Exception as e:\n        print(f"An error occurred during PDF sanitization: {e}")\n\n# Example usage\nsanitize_pdf("/path/to/uploaded/file.pdf", "/path/to/sanitized/file.pdf")