# native_app.py\nimport subprocess\nimport sys\nimport json\n\ndef run_external_process(command):\n    result = subprocess.run(command, shell=True)\n    return result.returncode\n\nif __name__ == "__main__":\n    input_data = sys.stdin.read()\n    command = json.loads(input_data).get("command")\n    exit_code = run_external_process(command)\n    print(json.dumps({"exitCode": exit_code}))