#!/usr/bin/env python3\nimport sys\nimport time\n\nuser_sessions = {}\n\ndef main():\n    while True:\n        line = sys.stdin.readline().strip()\n        if not line:\n            break\n        \n        client_ip = line.split()[0]\n\n        # Check if the user has seen the MOD page\n        if client_ip not in user_sessions:\n            user_sessions[client_ip] = time.time()\n            print("ERR")  # Redirect to MOD\n        else:\n            print("OK")  # Allow normal browsing\n\nif __name__ == "__main__":\n    main()