import fitz  # PyMuPDF\n\ndef find_offsets(pdf_path, search_string):\n    doc = fitz.open(pdf_path)\n    offsets = []\n    \n    for page_num in range(len(doc)):\n        page = doc.load_page(page_num)\n        text_instances = page.search_for(search_string)\n        \n        for inst in text_instances:\n            x0, y0, x1, y1 = inst\n            offsets.append({\n                'top': y1,\n                'left': x0,\n                'bottom': y0,\n                'right': x1\n            })\n    \n    return offsets\n\n# Example usage\npdf_path = 'example.pdf'\nsearch_string = 'somestring'\noffsets = find_offsets(pdf_path, search_string)\nprint(offsets)