# rules.py\n\nclass Rule:\n    def apply(self, data):\n        raise NotImplementedError("Subclasses should implement this method")\n\n\nclass KeywordRepetitionRule(Rule):\n    def apply(self, data):\n        keyword1 = data.get("keyword1", "")\n        keyword5 = data.get("keyword5", "")\n        if keyword1 and keyword1 * 3 in keyword5:\n            # Define the action for this rule\n            return "Keyword 1 repeated 3 times in Keyword 5"\n        return None\n\n# Add more rules as needed