# Open the text file in read mode\nwith open('english2german.txt', 'r') as english2german:\n    englist = []\n    gerlist = []\n\n    # Read each line in the file\n    for line in english2german:\n        # Split the line into two parts\n        eng, ger = line.split()\n        # Append the English and German words to their respective lists\n        englist.append(eng)\n        gerlist.append(ger)\n\n# Print the lists\nprint("English List:", englist)\nprint("German List:", gerlist)