# Load the bad words list\ndef load_bad_words(file_path):\n    with open(file_path, 'r') as file:\n        bad_words = {line.strip().lower() for line in file}\n    return bad_words\n\n# Function to filter bad words from text\ndef filter_bad_words(text, bad_words):\n    words = text.split()\n    filtered_words = [word if word.lower() not in bad_words else '*' * len(word) for word in words]\n    return ' '.join(filtered_words)\n\n# Example usage\nbad_words = load_bad_words('bad_words.txt')\ntext = "This is an example text with badword1 and badword2."\nfiltered_text = filter_bad_words(text, bad_words)\nprint(filtered_text)