# Python (Flask) example\n\nfrom flask import Flask, request, jsonify\nimport uuid\n\napp = Flask(__name__)\n\n# In-memory storage for demonstration\ntemporary_objects = {}\nattributes = []\n\n@app.route('/create_temporary_object', methods=['POST'])\ndef create_temporary_object():\n    data = request.get_json()\n    object_id = str(uuid.uuid4())\n    temporary_objects[object_id] = data['name']\n    return jsonify({'object_id': object_id})\n\n@app.route('/save_attribute', methods=['POST'])\ndef save_attribute():\n    data = request.get_json()\n    attribute = {\n        'id': str(uuid.uuid4()),\n        'name': data['name'],\n        'object_id': data['object_id']\n    }\n    attributes.append(attribute)\n    return jsonify({'status': 'success'})\n\nif __name__ == '__main__':\n    app.run(debug=True)