# urls.py\n   from django.urls import path\n   from django.utils.translation import gettext_lazy as _\n   from . import views\n\n   def get_lang_word():\n       from django.utils import translation\n       lang_code = translation.get_language()\n\n       if lang_code == 'fr':\n           return 'Livres'\n       elif lang_code == 'de':\n           return 'Bücher'\n       elif lang_code == 'jp':\n           return '和書'\n       else:\n           return 'books'\n\n   lang_word = get_lang_word()\n\n   urlpatterns = [\n       path(f'{lang_word}/', views.books_view, name='books'),\n       # other patterns...\n   ]