# Load words into a set\nwith open('path/to/your/wordlist.txt', 'r') as file:\n    words_set = set(word.strip() for word in file)\n\n# Function to check if a word is in the set\ndef is_word_in_english(word):\n    return word in words_set\n\n# Example usage\nword = "example"\nif is_word_in_english(word):\n    print(f"{word} is in English.")\nelse:\n    print(f"{word} is not in English.")