# myapp/tests/test_schema.py\n\nfrom django.test import TestCase\nfrom django.apps import apps\nfrom django.db import connection\n\nclass SchemaTests(TestCase):\n    def test_schema_sync(self):\n        # Get all models\n        models = apps.get_models()\n\n        with connection.cursor() as cursor:\n            for model in models:\n                # Get the table name for the model\n                table_name = model._meta.db_table\n\n                # Get the existing columns in the database table\n                cursor.execute(f"SELECT column_name FROM information_schema.columns WHERE table_name = '{table_name}';")\n                db_columns = set(row[0] for row in cursor.fetchall())\n\n                # Get the columns defined in the model\n                model_columns = set(field.column for field in model._meta.fields)\n\n                # Compare the two sets\n                self.assertEqual(db_columns, model_columns, f"Schema mismatch for table {table_name}")