# Example of using libp2p in Python\n# This is a simplified example, actual usage may vary\nfrom libp2p import new_node\nfrom libp2p.network.stream.net_stream import INetStream\n\nasync def run():\n    node = await new_node()\n    await node.listen("/ip4/127.0.0.1/tcp/8000")\n\n    async def handler(stream: INetStream):\n        data = await stream.read()\n        print("Data received:", data)\n        await stream.write(b"Hello, peer!")\n    \n    node.set_stream_handler("/myprotocol/1.0.0", handler)\n    await node.start()\n\n# Use asyncio to run the above function\nimport asyncio\nasyncio.run(run())