# Client-side code\nimport socket\n\ndef start_client():\n    server_socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)\n    server_socket.bind(('localhost', 65432))\n    server_socket.listen()\n    while True:\n        conn, addr = server_socket.accept()\n        with conn:\n            print('Connected by', addr)\n            data = conn.recv(1024)\n            if data:\n                print('Alert:', data.decode('utf-8'))\n                # Here you can also use a GUI library to display the alert\n\n# Example usage\nstart_client()