# Smalltalk method\nsmalltalk_method = """\nObject subclass: Person [\n    | name age |\n    \n    Person class >> new [\n        ^super new initialize.\n    ]\n    \n    Person >> initialize [\n        name := ''.\n        age := 0.\n        ^self.\n    ]\n    \n    Person >> setName: aName [\n        name := aName.\n    ]\n    \n    Person >> setAge: anAge [\n        age := anAge.\n    ]\n    \n    Person >> display [\n        ^'Name: ', name, ', Age: ', age printString.\n    ]\n]\n"""\n\n# Corresponding Python code generation\npython_code = """\nclass Person:\n    def __init__(self):\n        self.name = ''\n        self.age = 0\n    \n    @classmethod\n    def new(cls):\n        return cls()\n    \n    def set_name(self, name):\n        self.name = name\n    \n    def set_age(self, age):\n        self.age = age\n    \n    def display(self):\n        return f'Name: {self.name}, Age: {self.age}'\n"""\n\nprint(python_code)