import fitz  # PyMuPDF\nimport os\n\ndef extract_title(pdf_path):\n    doc = fitz.open(pdf_path)\n    first_page = doc.load_page(0)\n    text = first_page.get_text("text")\n    \n    # Extracting the title (for example, assume the title is the first line)\n    title = text.split('\n')[0].strip()\n    \n    # Sanitize the title to create a valid filename\n    valid_chars = "-_.() %s%s" % (string.ascii_letters, string.digits)\n    filename = ''.join(c for c in title if c in valid_chars)\n    \n    return filename\n\ndef rename_pdf(pdf_path):\n    title = extract_title(pdf_path)\n    new_path = os.path.join(os.path.dirname(pdf_path), title + ".pdf")\n    os.rename(pdf_path, new_path)\n    print(f'Renamed: {pdf_path} to {new_path}')\n\n# Example usage\npdf_path = 'path/to/your/downloaded/paper.pdf'\nrename_pdf(pdf_path)