import fitz  # PyMuPDF\n    from pytesseract import image_to_string\n    from PIL import Image\n    import io\n\n    input_pdf_path = 'input.pdf'\n    output_pdf_path = 'output_searchable.pdf'\n\n    # Open the input PDF\n    doc = fitz.open(input_pdf_path)\n    pdf_writer = fitz.open()\n\n    for page_num in range(len(doc)):\n        page = doc.load_page(page_num)\n        pix = page.get_pixmap()\n        img = Image.open(io.BytesIO(pix.tobytes()))\n\n        # Perform OCR on the image\n        text = image_to_string(img)\n        \n        # Create a new page with the OCR text\n        new_page = pdf_writer.new_page(width=page.rect.width, height=page.rect.height)\n        new_page.insert_text((72, 72), text, fontsize=12)\n    \n    # Save the searchable PDF\n    pdf_writer.save(output_pdf_path)