# test_my_module.py\nimport unittest\nfrom unittest.mock import patch\nfrom my_module import outer\n\nclass TestOuterFunction(unittest.TestCase):\n    @patch('my_module.outer.inner')\n    def test_outer(self, mock_inner):\n        mock_inner.return_value = "mocked result"\n        result = outer()\n        self.assertEqual(result, "mocked result")  # Example assertion\n\nif __name__ == '__main__':\n    unittest.main()