import fitz  # PyMuPDF\n\ndef extract_note_annotations(pdf_path):\n    # Open the PDF file\n    document = fitz.open(pdf_path)\n    annotations = []\n\n    # Iterate through each page in the PDF\n    for page_num in range(len(document)):\n        page = document.load_page(page_num)\n        # Get all annotations on the page\n        annot = page.first_annot\n        while annot:\n            if annot.type[0] == 2:  # Type 2 corresponds to text annotations (notes)\n                annotations.append(annot.info["content"])\n            annot = annot.next\n    \n    return annotations\n\n# Example usage\npdf_path = "example.pdf"\nnotes = extract_note_annotations(pdf_path)\nfor note in notes:\n    print(note)