# Define the chessboard as an 8x8 array\nchessboard = [\n    ['r', 'n', 'b', 'q', 'k', 'b', 'n', 'r'],\n    ['p', 'p', 'p', 'p', 'p', 'p', 'p', 'p'],\n    ['.', '.', '.', '.', '.', '.', '.', '.'],\n    ['.', '.', '.', '.', '.', '.', '.', '.'],\n    ['.', '.', '.', '.', '.', '.', '.', '.'],\n    ['.', '.', '.', '.', '.', '.', '.', '.'],\n    ['P', 'P', 'P', 'P', 'P', 'P', 'P', 'P'],\n    ['R', 'N', 'B', 'Q', 'K', 'B', 'N', 'R']\n]\n\n# Initialize lists for empty, black, and white positions\nempty_positions = []\nblack_positions = []\nwhite_positions = []\n\n# Populate the lists by iterating through the chessboard\nfor row in range(8):\n    for col in range(8):\n        piece = chessboard[row][col]\n        coordinate = (row, col)\n        \n        if piece == '.':\n            empty_positions.append(coordinate)\n        elif piece.islower():  # assuming lowercase for black pieces\n            black_positions.append(coordinate)\n        elif piece.isupper():  # assuming uppercase for white pieces\n            white_positions.append(coordinate)\n\n# Now you have separate lists for empty, black, and white positions\nprint("Empty Positions:", empty_positions)\nprint("Black Positions:", black_positions)\nprint("White Positions:", white_positions)