#!/usr/bin/env python\n\nimport pygtk\npygtk.require('2.0')\nimport gtk\nimport gtk.glade\n\nclass TestClass:\n    def __init__(self):\n        self.wTree = gtk.glade.XML("test.glade")\n        self.wTree.signal_autoconnect(self)\n\n    def on_TestClass_destroy(self, widget, data=None):\n        gtk.main_quit()\n\nif __name__ == "__main__":\n    window = TestClass()\n    gtk.main()