import fitz  # PyMuPDF\n\n   def is_color_page(page):\n       pix = page.get_pixmap()\n       # Check if all pixels are grayscale\n       for y in range(pix.height):\n           for x in range(pix.width):\n               r, g, b = pix.pixel(x, y)\n               if r != g or g != b:\n                   return True  # Color page\n       return False  # Black-and-white page\n\n   def analyze_pdf(file_path):\n       doc = fitz.open(file_path)\n       color_pages = []\n       bw_pages = []\n\n       for page_num in range(len(doc)):\n           page = doc.load_page(page_num)\n           if is_color_page(page):\n               color_pages.append(page_num + 1)\n           else:\n               bw_pages.append(page_num + 1)\n       \n       return color_pages, bw_pages\n\n   if __name__ == "__main__":\n       file_path = "your_document.pdf"\n       color_pages, bw_pages = analyze_pdf(file_path)\n\n       print("Color Pages:", color_pages)\n       print("Black-and-White Pages:", bw_pages)