# Adding a new Work with VariantWorkTitles\nnew_work = Work(\n    uniform_title='Example Title',\n    created_date='2023-10-10',\n    context='Example context',\n    distinguishing_characteristics='Example characteristics',\n    variant_titles=[\n        VariantWorkTitle(title='Variant Title 1'),\n        VariantWorkTitle(title='Variant Title 2')\n    ]\n)\n\nsession.add(new_work)\nsession.commit()\n\n# Querying a Work and accessing its variant titles\nwork_with_variants = session.query(Work).filter_by(id=new_work.id).first()\nprint(work_with_variants.uniform_title)\nfor variant in work_with_variants.variant_titles:\n    print(variant.title)