#!/usr/bin/env python3\n   import cgi\n   import cgitb\n   import os\n\n   cgitb.enable()\n\n   form = cgi.FieldStorage()\n   choice = form.getvalue('choice')\n\n   # Save the choice to a temporary file or pass it back to the main script\n   with open('/tmp/choice.txt', 'w') as f:\n       f.write(choice)\n\n   print("Content-Type: text/html")\n   print()\n   print("<html><body>")\n   print("<h1>Choice received: {}</h1>".format(choice))\n   print("</body></html>")