import fitz  # PyMuPDF\n\n# Open the PDF\npdf_document = fitz.open("file.pdf")\n\n# Access the metadata\nmetadata = pdf_document.metadata\n\n# Modify the metadata\nmetadata['keywords'] = 'example, keywords'\nmetadata['title'] = 'New Title'\n\n# Set the modified metadata\npdf_document.set_metadata(metadata)\n\n# Save the changes to a new file\npdf_document.save("updated_file.pdf")\n\n# Close the document\npdf_document.close()