# First, install the kmodes package if you haven't already\n# !pip install kmodes\n\nfrom kmodes.kprototypes import KPrototypes\nimport numpy as np\n\n# Example data: a mix of categorical (discrete) and numerical (continuous) attributes\ndata = np.array([\n    ['Male', 25, 'USA', 50000],\n    ['Female', 30, 'Canada', 60000],\n    ['Male', 35, 'UK', 70000],\n    ['Female', 40, 'USA', 80000],\n    ['Male', 45, 'Canada', 90000]\n])\n\n# Categorical columns (0-indexed)\ncategorical_columns = [0, 2]\n\n# Create K-Prototypes model\nkproto = KPrototypes(n_clusters=2, init='Cao', verbose=2)\n\n# Fit the model to the data\nclusters = kproto.fit_predict(data, categorical=categorical_columns)\n\n# Print the resulting clusters\nprint(clusters)\n\n# You can also print the cluster centroids\nprint(kproto.cluster_centroids_)