import fitz  # PyMuPDF\n\ndef extract_images_with_positions(pdf_path):\n    document = fitz.open(pdf_path)\n    images = []\n\n    for page_number in range(len(document)):\n        page = document[page_number]\n        image_list = page.get_images(full=True)\n\n        for image_index, img in enumerate(image_list):\n            xref = img[0]\n            base_image = document.extract_image(xref)\n            image_bytes = base_image["image"]\n            image_ext = base_image["ext"]\n            image_info = {\n                "page_number": page_number,\n                "xref": xref,\n                "extension": image_ext,\n                "bytes": image_bytes,\n                "bbox": img[4],\n                "width": img[2],\n                "height": img[3]\n            }\n            images.append(image_info)\n            # Save the image to a file (optional)\n            with open(f"image_{page_number}_{image_index}.{image_ext}", "wb") as img_file:\n                img_file.write(image_bytes)\n\n    return images\n\npdf_path = "your_pdf_file.pdf"\nimages_with_positions = extract_images_with_positions(pdf_path)\n\nfor image in images_with_positions:\n    print(f"Page: {image['page_number']}, Position: {image['bbox']}, Size: {image['width']}x{image['height']}")