# Python code for business logic and additional processing\nimport requests\n\ndef process_users(users):\n    for user in users:\n        # Perform additional checks and processing\n        if some_condition(user):\n            notify_user(user)\n\ndef some_condition(user):\n    # Business logic\n    return user['email'].endswith('@example.com')\n\ndef notify_user(user):\n    # Integration with an external service\n    requests.post('https://api.example.com/notify', json=user)