class ApplicationRecord < ActiveRecord::Base\n  self.abstract_class = true\n  \n  protected\n  \n  def log_activity\n    # logging activity\n    Rails.logger.info("Activity logged by #{self.class.name}")\n  end\nend\n\nclass User < ApplicationRecord\n  def log_user_activity\n    log_activity\n  end\nend\n\nclass Admin < User\n  def log_admin_activity\n    log_activity\n  end\nend\n\nuser = User.new\nadmin = Admin.new\n\nuser.log_user_activity     # This works\nadmin.log_admin_activity   # This works\nuser.log_activity          # Raises NoMethodError\nadmin.log_activity         # Raises NoMethodError