class PrivateAssetsController < ApplicationController\n     before_action :authenticate_user!  # Ensure user is authenticated\n\n     def show\n       file_path = Rails.root.join('app', 'assets', 'private', params[:filename])\n       if File.exist?(file_path)\n         send_file file_path, type: 'application/x-shockwave-flash', disposition: 'inline'\n       else\n         render status: :not_found, plain: "404 Not Found"\n       end\n     end\n   end