class AddressPdf < Prawn::Document\n  def initialize(addresses)\n    super(page_size: 'LETTER', margin: 50)\n    @addresses = addresses\n    address_labels\n  end\n\n  def address_labels\n    move_down 20\n    @addresses.each_slice(15) do |address_slice| \n      address_slice.each do |address|\n        text "#{address.name}\n#{address.street}\n#{address.city}, #{address.state} #{address.zip}", size: 12, leading: 2\n        move_down 20\n      end\n      start_new_page unless address_slice == @addresses.last(15)\n    end\n  end\nend