# check_email_in_db.rb\nrequire 'active_record'\n\n# Establish database connection\nActiveRecord::Base.establish_connection(\n  adapter: 'postgresql',\n  database: 'your_database',\n  username: 'your_username',\n  password: 'your_password',\n  host: 'localhost'\n)\n\n# Check for the test email\ntest_email = EmailLog.where(subject: 'Test Email').order(created_at: :desc).first\nexit(test_email ? 0 : 1)