class ApplicationController < ActionController::Base\n  # Define a rescue handler for exceptions\n  rescue_from StandardError, with: :handle_ajax_error\n\n  private\n\n  def handle_ajax_error(exception)\n    # Log the error for debugging purposes\n    logger.error exception.message\n    \n    respond_to do |format|\n      format.js { render 'shared/ajax_error', status: :internal_server_error }\n    end\n  end\nend