class UserController < ApplicationController\n     protect_from_forgery with: :exception\n\n     def bulkaction\n       if request.post?\n         ids = params[:ids]\n         # Process the ids as needed\n         Rails.logger.info(ids) # For testing purposes\n         render json: { success: true, ids: ids }\n       end\n\n       Rails.logger.info("This function was accessed and ran.")\n     end\n   end