class GradesController < ApplicationController\n     def index\n       @grades = Grade.all\n       render json: @grades\n     end\n\n     def update\n       # Process the changes sent from the client\n       params[:changes].each do |change|\n         # Find and update the grade record accordingly\n         grade = Grade.find(change[0])\n         grade.update(change[1] => change[3])\n       end\n\n       # Recalculate averages and letter grades\n       updated_grades = Grade.all\n       render json: updated_grades\n     end\n   end