require 'yaml'\n\nclass Foo\n  attr_accessor :bar\n\n  def initialize\n    @bar = 0\n  end\n\n  def set_defaults\n    @bar ||= 0\n  end\nend\n\n# Save object to YAML file\nfoo = Foo.new\nFile.open('foo.yml', 'w') { |file| file.write(foo.to_yaml) }\n\n# Load object from YAML file\nloaded_foo = YAML.load_file('foo.yml')\n\n# Set default values for instance variables\nloaded_foo.set_defaults\n\nputs loaded_foo.bar  # Output should be 0