class CommentsController < ApplicationController\n  before_action :set_comment, only: [:show, :edit, :update, :destroy, :moderate]\n  before_action :authenticate_user!\n\n  def create\n    @comment = Comment.new(comment_params)\n    @comment.user = current_user\n    authorize @comment\n    if @comment.save\n      redirect_to @comment, notice: 'Comment was successfully created.'\n    else\n      render :new\n    end\n  end\n\n  def update\n    authorize @comment\n    if @comment.update(comment_params)\n      redirect_to @comment, notice: 'Comment was successfully updated.'\n    else\n      render :edit\n    end\n  end\n\n  def destroy\n    authorize @comment\n    @comment.destroy\n    redirect_to comments_url, notice: 'Comment was successfully destroyed.'\n  end\n\n  def moderate\n    authorize @comment, :moderate?\n    @comment.update(status: 'moderator')\n    redirect_to @comment, notice: 'Comment was successfully moderated.'\n  end\n\n  private\n\n  def set_comment\n    @comment = Comment.find(params[:id])\n  end\n\n  def comment_params\n    params.require(:comment).permit(:content, :status)\n  end\nend