require 'sinatra'\nrequire 'thin'\n\nset :server, 'thin'\n\nget '/' do\n  content_type 'multipart/x-mixed-replace;boundary="rn9012"'\n\n  stream do |out|\n    out << "--rn9012\r\n"\n    out << "Content-type: application/xml\r\n\r\n"\n    out << "<?xml version='1.0'?>\n"\n    out << "<content>First Part</content>\n"\n    out << "--rn9012\r\n"\n    \n    sleep 5\n    \n    out << "Content-type: application/xml\r\n\r\n"\n    out << "<?xml version='1.0'?>\n"\n    out << "<content>Second Part</content>\n"\n    out << "--rn9012--\r\n"\n  end\nend\n\n# To run this Sinatra app, save it to a file (e.g., app.rb) and run it with:\n# ruby app.rb