class Publicprofile < ApplicationRecord\n  belongs_to :user\n  has_many :emails\n  has_many :phonenumbers\n\n  accepts_nested_attributes_for :emails, allow_destroy: true\n  accepts_nested_attributes_for :phonenumbers, allow_destroy: true\nend\n\nclass Privateprofile < ApplicationRecord\n  belongs_to :user\n  has_many :addresses\n  has_many :creditcards\n\n  accepts_nested_attributes_for :addresses, allow_destroy: true\n  accepts_nested_attributes_for :creditcards, allow_destroy: true\nend