require 'nokogiri'\n\n# Example XML document as a string\nxml_data = <<-XML\n<?xml version="1.0" encoding="UTF-8"?>\n<!DOCTYPE items [\n  <!ELEMENT items (item+)>\n  <!ELEMENT item (title, description)>\n  <!ELEMENT title (#PCDATA)>\n  <!ELEMENT description (#PCDATA)>\n]>\n<items>\n  <item>\n    <title>Item 1 Title</title>\n    <description>Item 1 Description</description>\n  </item>\n  <item>\n    <title>Item 2 Title</title>\n    <description>Item 2 Description</description>\n  </item>\n</items>\nXML\n\n# Parse the XML document\ndoc = Nokogiri::XML(xml_data)\n\n# Define a class to represent the items\nclass Item\n  attr_accessor :title, :description\n\n  def initialize(title, description)\n    @title = title\n    @description = description\n  end\nend\n\n# Extract items from the XML document and create instances of the Item class\nitems = doc.xpath('//item').map do |item_node|\n  title = item_node.xpath('title').text\n  description = item_node.xpath('description').text\n  Item.new(title, description)\nend\n\n# Accessing the first item's title\nputs items[0].title  # Output: Item 1 Title