<!-- app/views/posts/show.html.erb -->\n    <div id="star-rating" data-rateable-id="<%= @post.id %>" data-rateable-type="Post"></div>\n    <div id="current-rating">\n      <%= @post.ratings.average(:score).to_f.round(2) %> / 5\n    </div>\n\n    <%= form_with model: Rating.new, url: ratings_path, remote: true, id: 'rating-form' do |f| %>\n      <%= f.hidden_field :rateable_id, value: @post.id %>\n      <%= f.hidden_field :rateable_type, value: 'Post' %>\n      <%= f.hidden_field :score %>\n    <% end %>