# config/breadcrumbs.rb\n\n    # Home breadcrumb\n    crumb :root do\n      link "Home", root_path\n    end\n\n    # Example for a user profile page\n    crumb :user_profile do |user|\n      link "Profile", user_profile_path(user)\n      parent :root\n    end\n\n    # Example for a messages page\n    crumb :user_messages do |user|\n      link "My Incoming Messages", user_messages_path(user)\n      parent :user_profile, user\n    end