# scripts/performance/test_session.rb\n\nrequire 'net/http'\nrequire 'uri'\n\nuri = URI.parse("http://localhost:3000/path/to/your/action")\nrequest = Net::HTTP::Get.new(uri)\nrequest["Cookie"] = "your-session-cookie"\n\nreq_options = {\n  use_ssl: uri.scheme == "https",\n}\n\nresponse = Net::HTTP.start(uri.hostname, uri.port, req_options) do |http|\n  http.request(request)\nend\n\nputs response.body