require 'fileutils'\n\ndef copy_with_progress(src, dest)\n  total_size = File.size(src)\n  copied_size = 0\n  buffer_size = 1024 * 1024 # 1MB buffer\n\n  File.open(src, 'rb') do |source_file|\n    File.open(dest, 'wb') do |dest_file|\n      while (buffer = source_file.read(buffer_size))\n        dest_file.write(buffer)\n        copied_size += buffer.size\n        progress = (copied_size.to_f / total_size) * 100\n        print "\rCopying: #{progress.round(2)}%"\n      end\n    end\n  end\n\n  puts "\nCopy completed!"\nend\n\n# Example usage\nsource_path = 'path/to/source/file'\ndestination_path = 'path/to/destination/file'\ncopy_with_progress(source_path, destination_path)