require 'yaml'\n    require 'erb'\n\n    def parse_template(file_path)\n      content = File.read(file_path)\n      if content =~ /\A---(.+?)---(.*)/m\n        metadata = YAML.load($1)\n        template_content = $2\n      else\n        metadata = {}\n        template_content = content\n      end\n      return metadata, template_content\n    end\n\n    file_path = 'path/to/your/template.erb'\n    metadata, template_content = parse_template(file_path)\n\n    @metadata = metadata\n    renderer = ERB.new(template_content)\n    result = renderer.result(binding)\n\n    puts result